import './style/App.css';
import React, { Component } from 'react';
import axios from 'axios'
import Navbar from './components/Navbar'
import AreasVista from './views/AreasVista'
import AuditoriasVista from './views/AuditoriasVista'
import { Route, BrowserRouter } from 'react-router-dom'

class App extends Component {
	state = {
		posts : []
	}

	postParams = {
		collection: "auditorias",
		filter: {}
	}

	vhangeView = (params) => {
		axios.post('http://127.0.0.1:5000/', params)
			.then(res => {
				// console.log(res.data);
				this.setState({
					posts: res.data
				});
			})
	}

	render() {
		return (
			<BrowserRouter>
				<div className="App">
					<Navbar />
					<Route exact path='/' component={AreasVista} />
					<Route path='/areas' component={AreasVista} />
					<Route path='/auditorias' component={AuditoriasVista} />
				</div>
			</BrowserRouter>
		);
	}
}

export default App;
