import React, { Component } from 'react';
import Zeph from './Zeph'
import AddZeph from './AddZeph'

class App extends Component {
  state = {
    names: [
      {
        user: "Zeph",
        year: "2021",
        id: "1"
      },
      {
        user: "Spectra",
        year: "2019",
        id: "2"
      },
      {
        user: "Rekieskat",
        year: "2017",
        id: "3"
      },
      {
        user: "Xacu",
        year: "2013",
        id: "4"
      }
    ]
  }

  addZeph = (zeph) => {
    //console.log(zeph)
    zeph.id = Math.random();
    let newZeph = [...this.state.names, zeph]

    this.setState({
      names: newZeph
    })
  }

  deleteZeph = (id) => {
    let arrNames = this.state.names.filter(name => {
      return name.id !== id
    })
    this.setState({
      names: arrNames
    })
  }

  render() {
    return (
      <div className="App">
        <p>Parece que funciona</p>
        <Zeph deleteZeph={ this.deleteZeph } names={this.state.names} />
        <AddZeph addZeph={ this.addZeph }/>
        {/* <Zeph name="Pablo" age="647" /> */}
      </div>
    );
  }
}

export default App;
