export const createAuditoria = (auditoria) => {
    return (dispatch, getState, {getFirebase, getFirestore}) => {
        const firestore = getFirestore();
        firestore.collection("auditorias").add({
            ...auditoria,
            createdBy: "NEO",
            createdAt: new Date()
        })

        dispatch({type: "CREATE_AUDITORIA"}, auditoria)
    }
}
