import './style/App.css';
import React, { Component } from 'react';
import axios from 'axios'
import Navbar from './components/Navbar'
import AreasVista from './views/AreasVista'
import AuditoriasVista from './views/AuditoriasVista'
import { Route, BrowserRouter } from 'react-router-dom'

class App extends Component {
	state = {
		posts : [
			{
				area: "DummyObject",
				_id: "1"
			}
		],
		params: {
			collection: "areas",
			filter: {}
		}
	}

	changeView = () => {
		axios.post('http://127.0.0.1:5000/', this.state.params)
			.then(res => {
				console.log(res.data);
				this.setState({
					posts: res.data
				});
			})
	}

	changeParams = (params) => {
		this.setState({
			params: params
		})
	}

	routes = [
		{
			path: "/auditorias",
			component: AuditoriasVista,
			changeParams: this.changeParams,
			data: this.state.posts
		},
		{
			path: "/areas",
			component: AreasVista,
			changeParams: this.changeParams,
			data: this.state.posts
		}
	]

	render() {
		return (
			<BrowserRouter>
				<div className="App">
					<Navbar />
					{/* <Route exact path='/' component={AreasVista} /> */}
					{this.routes.map(({path, component: C, changeParams}) => (
						<Route
							path={path}
							render={
								(props) => <C {...props} 
								changeView={this.changeView} 
								changeParams={this.changeParams} 
								posts={this.state.posts} />
							}
						/>
					))}
				</div>
			</BrowserRouter>
		);
	}
}

export default App;
