import React, { Component } from 'react';

class AddZeph extends Component {
    state = {
        user: null,
        year: null,
        id: null
    }

    handleChange = (e) => {
        this.setState({
            [e.target.id] : e.target.value
        })
    }

    handleSubmit = (e) => {
        e.preventDefault()
        //console.log(this.state)
        this.props.addZeph(this.state);
    }

    render() {
        return (
            <div className="AddZeph">
                <form onSubmit={ this.handleSubmit }>
                    <label htmlFor="user">User:</label>
                    <input type="text" id="user" onChange={this.handleChange}/>
                    <label htmlFor="year">Year:</label>
                    <input type="text" id="year" onChange={this.handleChange}/>
                    <button>Submit</button>
                </form>
            </div>
        );
    }
}

export default AddZeph;
