const MongoCon = () => {
    const MongoClient = require('mongodb').MongoClient;
    const uri = "mongodb+srv://gst-app:twwlLQqN2R9arQZA@gst-app.abjqw.mongodb.net/GST-APP?retryWrites=true&w=majority";
    const client = new MongoClient(uri, { useNewUrlParser: true, useUnifiedTopology: true });

    client.connect(err => {
        const collection = client.db("GST-APP").collection("areas");
        const arr = collection.find().toArray();
        // perform actions on the collection object
        client.close();
    });
    return (
        <div className="MongoTest">
            { arr }
        </div>
    );
}

export default MongoCon;
