import './style/App.css';
import React, { Component } from 'react';
import axios from 'axios'
import Navbar from './components/Navbar'
import AreasVista from './views/AreasVista'
import AuditoriasVista from './views/AuditoriasVista'
import { Route, BrowserRouter } from 'react-router-dom'

class App extends Component {
	state = {
		posts : [
			{
				nombre: "DummyObject",
				_id: "1"
			}
		]
	}

	changeView = (params) => {
		axios.post('http://127.0.0.1:5000/', params)
			.then(res => {
				console.log(res.data);
				this.setState({
					posts: res.data
				});
			})
	}

	routes = [
		{
			path: "/auditorias",
			component: AuditoriasVista,
			changeView: this.changeView,
			data: this.state.posts
		},
		{
			path: "/areas",
			component: AreasVista,
			changeView: this.changeView,
			data: this.state.posts
		}
	]

	render() {
		return (
			<BrowserRouter>
				<div className="App">
					<Navbar />
					{/* <Route exact path='/' component={AreasVista} /> */}
					{this.routes.map(({path, component: C, changeView}) => (
						<Route
							path={path}
							render={(props) => <C {...props} changeView={changeParams} posts={this.state.posts}/>}
						/>
					))}
				</div>
			</BrowserRouter>
		);
	}
}

export default App;
